import React, {Component} from 'react';

class Cart extends Component {
    state ={
        cart: []
    }
    componentDidMount() {
        this.setState({
            cart: this.loadCart()
        })
    }

    loadCart() {
        let cart = JSON.parse(localStorage.getItem("shoppingCart"));
        console.log(typeof (this.state.cart))
        if (cart == null)
            cart = [];
        return cart;
    };

    render(){

        return(
            <div>
                console.log(this.state.cart)
                {

            this.state.cart.map(item => {
                return(
                    <div>
                        <h2>{"ProductName: "+item.ProductName}</h2>
                        <p>{"Price: "+item.price}</p>
                        <input type="text" placeholder={item.count} onChange={item.count}/>
                    </div>
                )
            })
           } </div>
        )

    }
}



export default Cart;
