import React, {Component} from 'react';
import axios from "axios"
import qs from "qs"
class Delete extends Component {
      constructor(props) {
    super(props);
    this.state = {
        ProductName: ""
    };
  }
  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    });
  }
  handleSubmit = e => {
          let url;
          axios.delete(url, qs.stringify(this.state.ProductName))
              .then(res=> alert("deleted"))
  }
    render() {
        return (
            <div>
                <form >
                    <input type="text" name="ProductName" onChange={this.handleChange}/>
                     <button className="submitButton"
                    type="submit"
                    onClick={(e)=>this._handleSubmit(e)}>Delete</button>
                </form>
            </div>
        );
    }
}

export default Delete;
