import React from 'react';
import Slider from "react-slick";
class Item extends React.Component {
    state = {cart:[] }
    componentDidMount() {
        if (typeof (Storage))
            this.setState({
                    cart: localStorage.getItem("shoppingCart")
                }
            )

    }

    componentWillUnmount() {

        localStorage.setItem("shoppingCart", this.state.cart)
    }
    handleClick(a,b,c){
        console.log(a,b,c)
        // this.state.cart.push()

        this.setState({new:{ProductName: a, price:b, count:c}})
        this.state.cart.push(...this.state.cart, this.state.new)
        this.setState(this.state)
    }

    render(){
        return (
            <div className="item-list">
                {
                    this.props.items.map(item => {
                        // var settings = {
                        //   dots: true,s
                        //   infinite: true,
                        //   speed: 500,
                        //   slidesToShow: 1,
                        //   slidesToScroll: 1
                        // };
                        return (

                            <a  className="flip-card" >
                                <div className="flip-card-inner">
                                    <div className="flip-card-front">
                                        <img  src={item.Picture} alt="Avatar" width={"200px"} height={"200px"}  />
                                    </div>


                                    {/*console.log({item.pictures})*/}
                                    <div className="flip-card-back">
                                        <div>ProductName: { item.ProductName }</div>
                                        <div>Brand: { item.Brand }</div>
                                        <div>Weight: { item.Weight }</div>
                                        <div>price: { item.price }</div>

                                        {/*<div>{item.pic}</div>*/}
                                        <button onClick={
                                            this.handleClick(item.ProductName, item.price, 1)}

                                        >AddToCart</button>

                                    </div>
                                    {/*<hr />*/}
                                </div>
                            </a>

                        )
                    })
                }
            </div>
        );}
};

function saveCart(cart) {
    // localStorage.clear()
    localStorage.setItem("shoppingCart", JSON.stringify(cart));
    console.log(localStorage.getItem("shoppingCart"))
};

// function loadCart() {
//     var cart = JSON.parse(localStorage.getItem("shoppingCart"));
//     if (cart == null)
//         cart = [];
// };
export default Item
