import React, {Component} from 'react';

class Cart extends Component {
    render() {
        // var store = localStorage
        return (
            <div>
                { typeof(Storage)? localStorage.map(item => {
                    return(
                    <a href={"/products/" + item.ProductName}>
                        <h2>{"ProductName: "+item.ProductName}</h2>
                        <p>{"Price: "+item.price}</p>
                        <input type="text" placeholder={item.count} onChange={item.count+1}/>
                    </a>
                    )
                })
                :"your cart is empty"}
            </div>
        );
    }
}

export default Cart;
