import React from 'react';
 import Slider from "react-slick";
import {NavLink} from "react-router-dom";
const Items = ({items, deleteItem}) => {


  return (
    <div className="item-list">
      { 
        items.map(item => {
                // var settings = {
                //   dots: true,s
                //   infinite: true,
                //   speed: 500,
                //   slidesToShow: 1,
                //   slidesToScroll: 1
                // };
          return (

            <NavLink to={"http://localhost:3000/products/"+item.ProductName} className="flip-card"  key={item.id}>
              <div className="flip-card-inner">
                    <div className="flip-card-front">
                        <img  src={item.Picture} alt="Avatar" width={"200px"} height={"200px"}  />
                    </div>


              {/*console.log({item.pictures})*/}
              <div className="flip-card-back">
              <div>ProductName: { item.ProductName }</div>
              <div>Brand: { item.Brand }</div>
              <div>Weight: { item.Weight }</div>
                  <div>price: { item.price }</div>

              {/*<div>{item.pic}</div>*/}
              <button onClick={() => {deleteItem(item.id)}}>Delete item</button>
              </div>
              {/*<hr />*/}
            </div>
            </NavLink>

          )
        })
      }
    </div>
  );
};

export default Items
