import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

import Home from "./Navbar/Home"
import Leftbar from './Containers/Leftbar'
import Navbar from './Navbar/Navbar'
import Items from './Items/Items'
import Contacts from "./Navbar/Contacts"
import Category from "./components/Categories"
// import React, { Component } from 'react';
import { Route, BrowserRouter } from 'react-router-dom'
// import Home from './components/Home'
import About from './components/About'
// import Contact from './components/Contact'
import Products from './Product/Products'
import Product from "./Product/Product"
import Login from './Navbar/Login'
import Signup from './Navbar/Signup'

import axios from "axios";
// import {getCookie, setCookie} from "../Behivior/Cookie"
class App extends Component {
    state={
        user:null
    };

      componentDidMount() {
      let url = "http://localhost:8080/";
      axios.get(url,{withCredentials:true})
          .then(res=>this.setState({
              user : res.data.profile.username
          }

      ))
          // .then(res=> console.log(this.state))
    .catch(function (error) {
      alert(error)
    });
    }
    // getCookie(key) {
    //     const name = key + "=";
    //     const ca = document.cookie.split(';');
    //     for (let i = 0; i < ca.length; i++) {
    //         const c = ca[i].trim();
    //         if (c.indexOf(name) === 0) {
    //             return c.substring(name.length, c.length);
    //         }
    //     }
    //     return "";
    // }
    // componentDidMount() {
    //     // this.getCookie("user")?
    //     // this.setState({
    //     //     profile : this.getCookie("profile")
    //     // }):
    //
    //         axios.get("http://localhost:8080")
    //             .then(res=>this.setState({
    //                 user: res.data }
    //
    //         )).then(console.log(this.state))
    //           .catch(function (error) {
    //             alert(error)
    //           });
    // }
  render() {
    return (
      <BrowserRouter>
        <div className="App">
          <Navbar user={this.state.user}/>
          <Leftbar/>
          {/*<Items/>*/}
          {/*<Stuff/>*/}
          <Route exact path='/' component={Home}/>
          <Route path='/about' component={About} />
          {/*<Route path='/'/>*/}
            <Route exact path='/products' component={Products}/>
            <Route path='/login' component={Login}/>
            <Route path='/signup' component={Signup}/>
          <Route path='/contacts' component={Contacts} />
          <Route path="/category/:category_name" component={Category}/>
          <Route path="products/:product_name" component={Product}/>
          {/*<Route path='/:post_id' component={Post} />*/}
          {/*<Route path="/"/> */}
        </div>
      </BrowserRouter>
    );
  }
}

export default App;


