import React, {Component} from 'react';
import store from "storejs"
import Cart from "./Cart"
import Item from "../Items/Item"
import {Link} from "react-router-dom"
import axios from "axios"
import qs from "qs"
class Carts extends Component {
    state = {
        products: []
    }

    componentDidMount() {
        // this.setState(store.getAll().values())
        // console.log(this.state)
        // var list= store.get("products")
        // this.setState({products: JSON.parse(list)})
        // console.log(this.state)
        var temp = [];
        store.forEach((k, d) =>
                temp.push(JSON.parse(d))
            //     this.setState({
            //         products:this.state.products.push(d)
            // })
        )
        // console.log("Temp")
        // console.log(temp);
        this.setState({products: temp})
        // console.log(this.state.products[0])
        // this.state.products.map(pro=>{
        // console.log("Products:");
        // console.log(this.state.products)
        // store.clear()

        // })

    }

    total() {


        let total = 0
        store.forEach((k, d) =>{
            let value = JSON.parse(d);
            let price = value.price
            let count = value.count
            console.log("price");
            console.log(price)
            total = total + price*count

        })

            console.log(total)
        return total
    }

    getCookie(key) {
        const name = key + "=";
        const ca = document.cookie.split(';');
        for (let i = 0; i < ca.length; i++) {
            const c = ca[i].trim();
            if (c.indexOf(name) === 0) {
                return c.substring(name.length, c.length);
            }
        }
        return "";
    }

    nextPage(){
        let url="http://localhost:8080/checkout/myprofile";
        let temp=''
        let user=this.getCookie("user");
        temp=axios.get(url,qs.stringify({ username:user}))?
            temp="/checkout"
            :
            temp="/userinfo"
            // .then(res => alert(res.data)
            // ).catch(err=> alert("Carts userinfo"))
        // alert("remp"+temp)
        console.log("nextpage: " +temp)
        return temp
    }
    render() {

        let nextPage=this.nextPage()
        console.log("nextpage: " +nextPage)

        return (
            <div>
                <h1>My Cart</h1>
    {/*<Cart/>*/}<Cart items={this.state.products}/>
                <br/>
                <br/>
                <h1>Total: {this.total()}</h1>
                <a href="/cart"> <button>Update</button></a>
                <Link to={nextPage}><button>Check Out</button></Link>
            </div>
        );
    }
}

export default Carts;
