import React from 'react'
import "./Signup.css"
import axios from "axios";
import qs from "qs";
class Signup extends React.Component {
    state ={
        // message: ""
        username:"",
        password: "",
        repeatPassword : ""
    };

      handleChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

    handleSubmit = event => {
    event.preventDefault();
    if (this.state.password !== this.state.repeatPassword){
        this.setState({
            message: "password did not match"
        });
        return
    }
    let myObj = {
        username : this.state.username,
        password : this.state.password
    };
    let url = "http://localhost:8080/login";
    axios.post(url, qs.stringify(myObj))
        .then(res=>
            res.data ?
            this.setState({
            message: "username has taken"
        }

    ) : this.props.history.push("/")
        )

        // .then(res=> console.log(this.state.message[0].lastName))
  // .catch(function (error) {
  //   alert(error)
  // });
  }

    render() {
        return(
            <form className="form-signin" onSubmit={this.handleSubmit} >
                <div className="container">
                    <h1>Sign Up</h1>

                    <label htmlFor="inputUsername" ><b>Username</b></label>
                <br/>
                    <input type="text" id="inputUsername" name="username" className="form-control" placeholder="Username" required
                          onChange={this.handleChange} />
                        <br/>
                            <label htmlFor="psw"><b>Password</b></label>
                    <br/>
                            <input type="password" placeholder="Enter Password" name="password" onChange={this.handleChange} required/>
                        <br/>
                                <label htmlFor="psw-repeat"><b>Repeat Password</b></label>
                    <br/>
                                <input type="password" placeholder="Repeat Password" name="repeatPassword" onChange={this.handleChange} required/>
                    <br/>
                    <p style={{backgroundColor: "red"}}>{this.state.message}</p>
                                    <div className="clearfix">
                                        <button type="submit" className="signupbtn">Sign Up</button>
                                        <button type="button" className="cancelbtn">Cancel</button>
                                    </div>
                </div>
            </form>
        )
    }
}
export default Signup
